package org.firstinspires.ftc.teamcode;

import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.util.ElapsedTime;

import org.firstinspires.ftc.robotcore.external.ClassFactory;
import org.firstinspires.ftc.robotcore.external.navigation.VuforiaLocalizer;
import org.firstinspires.ftc.robotcore.external.navigation.VuforiaTrackables;

/**
 * Created by Steve on 10/15/2016.
 */

public class ImageTest extends LinearOpMode {

    private ElapsedTime runtime = new ElapsedTime();
    private VuforiaLocalizer vuforia;

    @Override
    public void runOpMode() throws InterruptedException {

        VuforiaLocalizer.Parameters parms = new VuforiaLocalizer.Parameters(R.id.cameraMonitorViewId);
        parms.cameraDirection = VuforiaLocalizer.CameraDirection.BACK;
        parms.vuforiaLicenseKey = "";
        parms.cameraMonitorFeedback = VuforiaLocalizer.Parameters.CameraMonitorFeedback.AXES;

        this.vuforia = ClassFactory.createVuforiaLocalizer(parms);

        VuforiaTrackables playingField = vuforia.loadTrackablesFromAsset("FTC_2016-17");

        playingField.get(0).setName("Wheels");
        playingField.get(1).setName("Tools");
        playingField.get(2).setName("Legos");
        playingField.get(3).setName("Gears");

        waitForStart();
        runtime.reset();

        playingField.activate();

        while (opModeIsActive()) {

        }
    }
}
